    private void btnDividirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDividirActionPerformed
        // TODO add your handling code here:
        float n1 = Float.parseFloat(txtNum.getText());
        float n2 = Float.parseFloat(txtDem.getText());
        // divisao /
        // modulo ou resto %
        float div = n1/n2;
        float resto = n1%n2;
        float r2 = (float) Math.sqrt(n1);
        float r3 = (float) Math.cbrt(n1);
        lblDiv.setText(Float.toString(div));
        lblResto.setText(Float.toString(resto));
        lblRaizSQR.setText(Float.toString(r2));
        lblRaizCUB.setText(Float.toString(r3));
    }//GEN-LAST:event_btnDividirActionPerformed

